mapper.select:
SELECT GET_ON_LONGITUDE AS 'lng', GET_ON_LATITUDE AS 'lat' , TIME_REPRE AS 'timeRepre'
FROM ${tableName}
WHERE GET_ON_LONGITUDE BETWEEN ${lngmin} AND ${lngmax}
AND GET_ON_LATITUDE BETWEEN ${latmin} AND ${latmax}
ORDER BY TIME_REPRE

SELECT
TIME_REPRE          as time,
count('TIME_REPRE') as count
from ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY TIME_REPRE;

SELECT
plateno          as number,
SUM(METER_MONEY) as income
FROM ${table}
GROUP BY PLATENO;

SELECT
plateno          as number,
SUM(METER_MONEY) as income
FROM ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY PLATENO;

SELECT
plateno          as number,
SUM(EMPTY_MILEAGE) as emptyMileage
FROM ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY PLATENO;

SELECT COUNT(*) AS 'count'
FROM ${tableName}
WHERE GEOHASH5 IN

    #{item}

GROUP BY TIME_REPRE
