mapper.select:
SELECT
TIME_REPRE          as time,
count('TIME_REPRE') as count
from ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY TIME_REPRE;

SELECT
PLATENO          as number,
SUM(METER_MONEY) as income
FROM ${table}
GROUP BY PLATENO;

SELECT
plateno          as number,
SUM(METER_MONEY) as income
FROM ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY PLATENO;

SELECT
plateno          as number,
SUM(EMPTY_MILEAGE) as emptyMileage
FROM ${table}
WHERE TIME_REPRE >= ${startTime}
AND TIME_REPRE <= ${endTime}
GROUP BY PLATENO;
